import React from "react"
import * as ReactDOMClient from "react-dom/client"

// ReactDOM.render(React.createElement('input', {
//   placeholder: "Help text",
//   onClick: () => console.log('click'),
//   onMouseEnter: () => console.log("Mouse Over")
// }), document.getElementById('exemple'))



const helpText = "Help Text!"

class Header extends React.Component {
  render() {
    return <header>Шапка сайта</header>
  }
}

class App extends React.Component {
  return() {
    <div className="block" >
      <Header />
      <h1>{this.helpText}</h1>
      <input placeholder={this.helpText} onClick={this.inputClick} onMouseEnter={this.mouseOver} />
      <p>{this.helpText === "Help Text!" ? "Yes" : "No"}</p>
    </div>
  }

  inputClick() { console.log("Click"); }
  mouseOver() { console.log("Mouse Over") }
}


const app = ReactDOMClient.createRoot(document.getElementById("exemple"))
app.render(<App />)
