import React from "react"

class Users extends React.Component {
    // users = [
    //     {
    //         id: 1,
    //         firstname: 'Bob',
    //         lastname: 'Goga',
    //         age: 40,
    //         bio: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Illo enim odit fuga, voluptatibus repellat rem.",
    //         isHappy: true
    //     },
    //     {
    //         id: 2,
    //         firstname: 'Katya',
    //         lastname: 'Doe',
    //         age: 25,
    //         bio: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Illo enim odit fuga, voluptatibus repellat rem.",
    //         isHappy: false
    //     }
    // ]

    render() {
        if (this.users.length > 0) {
            return (<div>
                {this.users.map((el => (<div className="user" key={el.id}>
                    <h3>{el.firstname} {el.lastname}</h3>
                    <p>{el.bio}</p>
                </div>)))}
            </div>)
        }
        else return (<div className="user">
            <h3>Пользователей нет</h3>
        </div>)
    }

}

export default Users
