import React, { useEffect, useState } from "react"

const Button = (props) => {
    const [click, setClick] = useState(0)

    useEffect(() => {
        document.title = `Вы нажали ${click}`
    })
    return (
        <button onClick={() => setClick(click + 1)}>{props.text} </button>
    )
}

Button.defaultProps = {
    text: "кнопка"
}

export default Button
