import React from "react"
import { IoCloseCircleSharp, IoHammerSharp } from 'react-icons/io5'

class User extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            users: [
                {
                    id: 1,
                    firstname: 'Bob',
                    lastname: 'Goga',
                    age: 40,
                    bio: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Illo enim odit fuga, voluptatibus repellat rem.",
                    isHappy: true
                },
                {
                    id: 2,
                    firstname: 'Katya',
                    lastname: 'Doe',
                    age: 25,
                    bio: "Lorem, ipsum dolor sit amet consectetur adipisicing elit. Illo enim odit fuga, voluptatibus repellat rem.",
                    isHappy: false
                }
            ]
        }
    }
    user = this.props.user
    render() {
        return (<div className="user" >
            <IoCloseCircleSharp className="delete-icon" />
            <IoHammerSharp className="edit-icon" />
            <h3>{this.user.firstname} {this.user.lastname}</h3>
            <p>{this.user.bio}</p>
            <b>{this.user.isHappy ? 'Счастлив' : 'несчастен'}</b>
        </div>)
    }

}

export default User
